import {useEffect, useMemo, useState} from 'react'
import SquareBlock from './components/SquareBlock'
import axios from 'axios'
import './App.css';

function App() {

  const [squares, setSquares] = useState({id: 'normal', field: 5}, {id:'middle', field:10});
  const [board, setBoard] = useState([]);

  const [currMode, setCurrMode] = useState({id: 'normal', field:5})
  const [isChoosen, setIsChoosen] = useState (false)

  const setBoardCount = (count) =>{
    const array = new Array (count * count).fill('');
    setBoard(array)
    setIsChoosen(true)
  }






  return (
    <div className="App">
      <button onClick={() => setBoardCount(5)}></button>
      {
        isChoosen ? (
          <div>
          < SquareBlock countSquare = { currMode.field } boardArray = {board} />

          </div>
        ) : null
      }
    </div>
  );
}

export default App;
