import {useEffect, useMemo, useState} from 'react'
import Square from './components/Square';
import axios from 'axios'
import './App.css';

function App() {

  const [squares, setSquares] = useState({id: 'normal', field: 5}, {id:'middle', field:10});
  const [currentStyle, setCurrentStyle] = useState(squares[0]);
  const [board, setBoard] = useState();
  const [mouthPath, setMouthPath] = useState([]);

  const setBoardCount = (count) =>{
    const array = new Array(count);
    for (let i = 0; i < count; i++){
      array[i] = new Array(count) 
      for (let j = 0; j < count; j++ ){
        array[i][j] = ''
      }
    }
    setBoard(array)
  }

  useMemo(()=>{

  }, [board])
  // useEffect(() => {
  //   setBoardCount(5)
  // }, [])

  const changeBox = (index) =>{
    let row = index / currentStyle.field;
    let column = index % currentStyle.field;

    const array = board;
    array[row][column] = board[row][column].includes()
  }
  return (
    <div className="App">
      <div className="blockOfSquares">
          {
            board.map((value, index) => <Square mouseEnter={changeBox(index)} className = {value} />)
          }
      </div>
      <div className="mousePath">
        <ul>
        </ul>
      </div>
    </div>
  );
}

export default App;
