import {useEffect, useMemo, useState} from 'react'
import SquareBlock from './components/SquareBlock'
import Selection from './components/Selection'
import axios from 'axios'
import './App.css';

function App() {

  const [squares, setSquares] = useState([{id:'Normal mode', field:5}, {id:'Medium mode', field: 10}, {id:'Strength mode',field:15}]);
  const [board, setBoard] = useState([]);

  const [currMode, setCurrMode] = useState({})
  const [isChoosen, setIsChoosen] = useState (false)

  const setBoardCount = (count) =>{
    const array = new Array (count * count).fill('');
    setBoard(array)
    setIsChoosen(true)
  }
  useEffect(() => {
    setBoardCount(currMode.field);
  }, [currMode])

  const changeMode = (id) => {
    const index = squares.findIndex( item => item.id === id );
    setCurrMode(squares[index]);
  }

  // const getFieldInfo = async () => {
  //   axios.get('http://demo1030918.mockable.io/')
  //   .then(response => response.json())
  //   .then(data => setSquares( data )).then(inf => console.log(squares))
  // }
  // getFieldInfo()

  return (
    <div className="App">
      <Selection squares={squares} setCurrMode={changeMode}/>
      {
        isChoosen ? (
          <div>
          < SquareBlock countSquare = { currMode.field } boardArray = {board} />

          </div>
        ) : null
      }
    </div>
  );
}

export default App;
