import Square from './components/Square.jsx';
import SquareBlock from './components/SquareBlock'
import MousePath from './components/MousePath'
import axios from 'axios'
import './App.css';

function App() {

  const [squares, setSquares] = useState({id: 'normal', field: 5}, {id:'middle', field:10});
  const [board, setBoard] = useState([]);
  const [mouseTrack, setMouseTrack] = useState([])
  const [currMode, setCurrMode] = useState({id: normal, field:5})
  const [isChoosen, setIsChoosen] = useState (false)

  const setBoardCount = (count) =>{
    const array = new Array (count * count).fill('');
    setBoard(array)
    setIsChoosen(true)
  }
  const addBlockToTrack = (index) => {
      const row_index = Math.trunc(index / countSquare);
      const column_index = index % countSquare;
      const tmp = mouseTrack;
      tmp.push({id:mouseTrack.length, row:row_index, column:column_index})
      setMouseTrack(tmp)
  }




  return (
    <div className="App">
      <button onClick={() => setBoardCount(5)}></button>
      {
        isChoosen ? (
          <div>
          < SquareBlock countSquare = { currMode.field } boardArray = {board} addBlockToTrack = {addBlockToTrack}/>
          < MousePath list = {mouseTrack} />
          </div>
        ) : null
      }
    </div>
  );
}

export default App;
