import React, {useState} from 'react'
import Square from './Square'
import './Square.css'

function SquareBlock({countSquare, mouseEnter, boardArray}) {
    const [mouseTrack, setMouseTrack] = useState([]);
    const addBlockToTrack = (index) => {
        const row_index = Math.trunc(index / countSquare);
        const column_index = index % countSquare;
        setMouseTrack(...mouseTrack, {id:mouseTrack.length, row:row_index, column:column_index})
    }
    return (
        <div className ="mainBlock" style ={{
            gridTemplateColumns: `repeat(${countSquare}, 1fr)`,
            gridTemplateRows: `repeat(${countSquare}, 1fr)`
        }}>
            {
                boardArray.map((value, index) => 
                    <Square id = {index} />) 
            }
            < MousePath list = {mouseTrack} />
        </div>
    )
}

export default SquareBlock
