import MousePath from './components/MousePath'
import axios from 'axios'
import './App.css';

function App() {

  const [squares, setSquares] = useState({id: 'normal', field: 5}, {id:'middle', field:10});
  const [currentStyle, setCurrentStyle] = useState(squares[0]);
  const [board, setBoard] = useState([]);
  const [mouthPath, setMouthPath] = useState([]);
  const [isChoosen, setIsChoosen] = useState (false)

  const setBoardCount = (count) =>{
    const array = new Array (count * count).fill('');
    console.log(array);
    setBoard(array)
    setIsChoosen(true)
  }



  function changeBox (e) {
    e.target.style.background = 'blue'
