    let column_index = index % 5;
    const array = board;
    array[index] = board[index].includes('blue') ? '' : 'blue';
    setBoard(array)
    setMouthPath(...mouthPath, {id:mouthPath.length, row:row_index, column: column_index})
    console.log(board, mouthPath);
  }

  return (
    <div className="App">
      <button onClick={() => setBoardCount(5)}></button>
      {
        isChoosen ? (
          <div>
          < SquareBlock countSquare = { 5 } boardArray = {board} mouseEnter = { () => changeBox } />
          < MousePath list = { mouthPath } />
          </div>
        ) : null
      }
    </div>
  );
}

export default App;
