import React, {useState, useEffect} from 'react'
import MousePath from './MousePath'
import Square from './Square'
import './Square.css'

function SquareBlock({countSquare, boardArray}) {
    const [mouseTrack, setMouseTrack] = useState([])
    const addBlockToTrack = (index) => {
        const row_index = Math.trunc(index / countSquare);
        const column_index = index % countSquare;
        const tmp = mouseTrack;
        tmp.push({id:mouseTrack.length, row:row_index, column:column_index})
        setMouseTrack(tmp)
        console.log(row_index,);
    }
    useEffect(() => {
        console.log(mouseTrack);
    }, [mouseTrack])
    return (
        <div>
            <div className ="mainBlock" style ={{
                gridTemplateColumns: `repeat(${countSquare}, 1fr)`,
                gridTemplateRows: `repeat(${countSquare}, 1fr)`
            }}>
                {
                    boardArray.map((value, index) => 
                        <Square key={index} id = {index} addPath={addBlockToTrack} />) 
                }
            </div>
            < MousePath list = {mouseTrack} />
        </div>
    )
}

export default SquareBlock
